import utils from"./utils.js";export default class steps{constructor(obj){this.step=0,this.size=Number(obj.size),this.container=obj.container}addSize(value){this.size+=Number(value)}display(){let ul=document.createElement("ul");ul.className="steps is-balanced has-gaps is-medium is-horizontal has-content-above has-content-centered";for(let i=0;i<this.size;i++){let li=utils.create("li",{className:"steps-segment"}),span=document.createElement("span");i===this.step?(span.className="steps-marker is-hollow",span.innerHTML=this.step+1,li.appendChild(span),li.className+=" is-active"):(span.className="steps-marker",li.appendChild(span)),ul.appendChild(li)}if(this.container.hasChildNodes()){let node=this.container.childNodes[0];this.container.replaceChild(ul,node)}else this.container.appendChild(ul)}nextStep(){return this.step++,this.display(),!(this.step>=this.size)&&this.step}}